CREATE OR REPLACE FUNCTION obter_se_volume_consiste ( cd_material_p bigint, qt_volume_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qt_volume_minimo_w	double precision;
qt_volume_maximo_w	double precision;
ds_retorno_w		varchar(255);
qt_regra_w		bigint;
qt_idade_w		double precision;

/* Busca os valores da regra */

c01 CURSOR FOR
	SELECT	coalesce(qt_volume_minimo,-1),
		coalesce(qt_volume_maximo,999)
	from	regra_volume_leite
	where	coalesce(cd_material,coalesce(cd_material_p,0)) = coalesce(cd_material_p,0)
	and	qt_idade_w between Obter_idade_leite(nr_sequencia,'MIN') and Obter_idade_leite(nr_sequencia,'MAX');


BEGIN

select	max(obter_idade(b.dt_nascimento,coalesce(b.dt_obito,clock_timestamp()),'DIA'))
into STRICT	qt_idade_w
from	pessoa_fisica b
where	b.cd_pessoa_fisica = cd_pessoa_fisica_p;

select	count(*)
into STRICT	qt_regra_w
from	regra_volume_leite
where	qt_idade_w between Obter_idade_leite(nr_sequencia,'MIN') and Obter_idade_leite(nr_sequencia,'MAX')
and	coalesce(cd_material,coalesce(cd_material_p,0)) = coalesce(cd_material_p,0);

if (qt_regra_w > 0) then
	open c01;
	loop
	fetch c01 into
		qt_volume_minimo_w,
		qt_volume_maximo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		if (coalesce(qt_volume_p,0) > coalesce(qt_volume_maximo_w,999)) then
			ds_retorno_w := wheb_mensagem_pck.get_texto(309950); -- O volume está acima da dose recomendada!
			exit;
		elsif (coalesce(qt_volume_p,0) < coalesce(qt_volume_minimo_w,0)) then
			ds_retorno_w := wheb_mensagem_pck.get_texto(309949); -- O volume está abaixo da dose recomendada!
			exit;
		else
			ds_retorno_w := null;
		end if;
	end loop;
	close c01;
else
	ds_retorno_w := null;
end if;

return(ds_retorno_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
