CREATE OR REPLACE FUNCTION obter_se_worklist_lib_cat (cd_categoria_p wl_item.cd_categoria%type) RETURNS bigint AS $body$
DECLARE


nr_seq_wl_item_w	wl_perfil.nr_sequencia%type := 0;
nr_seq_wl_item_ww	wl_perfil.nr_sequencia%type := 0;


BEGIN

if (cd_categoria_p IS NOT NULL AND cd_categoria_p::text <> '') then
	
	select	max(nr_sequencia)
	into STRICT	nr_seq_wl_item_ww
	from 	wl_item
	where	cd_categoria = cd_categoria_p;

	if (nr_seq_wl_item_ww > 0) then
			select	max(a.nr_sequencia)
			into STRICT	nr_seq_wl_item_w
			from 	wl_item a,
				wl_regra_worklist b,
				wl_regra_item c,
				wl_perfil d
			where	a.cd_categoria = cd_categoria_p
			and	a.nr_sequencia = b.nr_seq_item
			and	b.nr_sequencia = c.nr_seq_regra
			and	c.nr_sequencia = d.nr_seq_regra_item;
	end if;

end if;

return nr_seq_wl_item_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
