CREATE OR REPLACE FUNCTION obter_sigla_api_tie ( ds_nome_api_p text ) RETURNS varchar AS $body$
DECLARE


	ds_sigla_w varchar(255) := '';


BEGIN
	if (ds_nome_api_p IS NOT NULL AND ds_nome_api_p::text <> '') then
		case
			when ds_nome_api_p in ('DRUG','FOOD') then
				ds_sigla_w := 'A';
			when ds_nome_api_p in ('DISEASE') then
				ds_sigla_w := 'B';
			when ds_nome_api_p in ('TC_DUPLICATION','INGREDIENT_DUPLICATION') then
				ds_sigla_w := 'C';			
			when ds_nome_api_p in ('DRUG_DOSE_INTERACTIONS') then
				ds_sigla_w := 'D';
			when ds_nome_api_p in ('ROUTE_CONTRAINDICATIONS') then
				ds_sigla_w := 'E';
			when ds_nome_api_p in ('AGE_CONTRAINDICATION') then
				ds_sigla_w := 'F';
			when ds_nome_api_p in ('GENDER_CONTRAINDICATIONS') then
				ds_sigla_w := 'G';
			when ds_nome_api_p in ('PREGNANCY') then
				ds_sigla_w := 'H';
			when ds_nome_api_p in ('LACTATION') then
				ds_sigla_w := 'I';
			when ds_nome_api_p in ('DRUG_DISEASE_CONTRAINDICATIONS') then
				ds_sigla_w := 'DDC';
			when ds_nome_api_p in ('PRECAUTIONS') then
				ds_sigla_w := 'PE';
			else
				ds_sigla_w := '';
		end case;
	end if;

	return ds_sigla_w;

end;
$body$
