CREATE OR REPLACE FUNCTION obter_sinais_vitais_suep ( nr_sequencia_p bigint, nr_informacao_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, ie_opcao_p text default 'V') RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
V - Valor Informacao
T - Tooltip
D - Dor
*/
ds_informacao_w	varchar(255);
contador_w bigint;

c01 CURSOR FOR
	SELECT	c.dt_informacao,
           		c.vl_informacao,
		c.ds_tolltip,
    c.cd_escala_dor
	from	w_suep a,
		w_suep_item b,
		w_suep_item_data c
	where	b.nr_seq_suep = a.nr_sequencia
	and    	b.nr_sequencia = c.nr_seq_suep_item
	and    	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and    	a.nm_usuario = nm_usuario_p
	and    	b.ie_grupo_suep = 'ST'
	and    	c.nr_seq_suep_item = nr_sequencia_p
	ORDER BY c.dt_informacao desc;

BEGIN
contador_w := 0;

for r_c01_w in c01 loop
		begin		
		contador_w := contador_w+1;		
		if (contador_w = nr_informacao_p) then		
			if (ie_opcao_p = 'T') then
			  ds_informacao_w := r_c01_w.ds_tolltip;
      elsif (ie_opcao_p = 'D') then
        ds_informacao_w := r_c01_w.cd_escala_dor;
			else
			  ds_informacao_w := r_c01_w.vl_informacao;
			end if;
		end if;
		end;
end loop;

return ds_informacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
