CREATE OR REPLACE FUNCTION obter_sinal_vital_data_ref ( nr_atendimento_p bigint, dt_referencia_p timestamp, ie_sinal_p text ) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;


BEGIN

if (ie_sinal_p = 'Peso') then
	select	max(qt_peso)
	into STRICT	vl_retorno_w
	from	atendimento_sinal_vital
	where	nr_sequencia = (SELECT	coalesce(max(nr_sequencia),0)
				from	atendimento_sinal_vital
				where	(qt_peso IS NOT NULL AND qt_peso::text <> '')
				and	nr_atendimento	= nr_atendimento_p
				and	ie_situacao = 'A'
				and	dt_sinal_vital <= fim_dia(dt_referencia_p));
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
