							and	nr_atendimento	= nr_atend_alta_w 
							and	ie_situacao = 'A' 
							and	coalesce(IE_RN,'N')	= 'N');
			elsif (ie_sinal_p = 'FR') then 
				select	max(qt_freq_resp) 
				into STRICT	vl_retorno_w 
				from	atendimento_sinal_vital	 
