CREATE OR REPLACE FUNCTION obter_situacao_doenca (cd_doenca_p text) RETURNS varchar AS $body$
DECLARE

ie_situacao_w		varchar(1);

BEGIN

SELECT	   	coalesce(MAX(IE_SITUACAO), 'A')
into STRICT		ie_situacao_w
FROM	   	CID_DOENCA
WHERE	   	CD_DOENCA =   cd_doenca_p;

return	ie_situacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
