CREATE OR REPLACE FUNCTION obter_situacao_emprestimo_mat ( nr_emprestimo_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_material_w		double precision;
qt_emprestimo_w	double precision;



BEGIN

select	qt_emprestimo,
	qt_material
into STRICT	qt_emprestimo_w,
	qt_material_w
from	emprestimo_material
where	nr_emprestimo	= nr_emprestimo_p
and	nr_sequencia	= nr_sequencia_p;

if (qt_material_w = 0) then
	ds_retorno_w := 'B';

elsif (qt_emprestimo_w - qt_material_w > 0) then
	ds_retorno_w := 'A';

elsif (qt_emprestimo_w = qt_material_W) then
	ds_retorno_w := 'P';
end if;

return ds_retorno_w;

END;
