CREATE OR REPLACE FUNCTION obter_situacao_estoque_mat ( cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(10);
qt_estoque_w			double precision;
qt_estoque_minimo_w		double precision;
qt_estoque_maximo_w		double precision;
qt_estoque_maximo_w2		double precision := 0;
qt_ponto_pedido_w			double precision;
cd_material_estoque_w		integer;


BEGIN

select	a.cd_material_estoque,
	b.qt_estoque_minimo,
	b.qt_estoque_maximo,
	b.qt_ponto_pedido
into STRICT	cd_material_estoque_w,
	qt_estoque_minimo_w,
	qt_estoque_maximo_w,
	qt_ponto_pedido_w
from	material a,
	material_estab b
where	a.cd_material = b.cd_material
and	b.cd_Estabelecimento = cd_estabelecimento_p
and	a.cd_material = cd_material_p;

qt_estoque_maximo_w2 := qt_estoque_maximo_w * qt_estoque_maximo_w;

select	sum(qt_estoque)
into STRICT	qt_estoque_w
from	saldo_estoque
where	cd_estabelecimento = cd_estabelecimento_p
and	cd_material = cd_material_estoque_w
and	dt_mesano_referencia = trunc(clock_timestamp(),'mm');

if (qt_estoque_w <= 0) then
	ds_retorno_w := 'ZE';
elsif (qt_estoque_w < qt_estoque_minimo_w) then
	ds_retorno_w := 'MB';
elsif (qt_estoque_w < qt_ponto_pedido_w) then
	ds_retorno_w := 'BA';
elsif (qt_estoque_w >= qt_ponto_pedido_w) and (qt_estoque_w <= qt_estoque_maximo_w) then
	ds_retorno_w := 'ID';
elsif (qt_estoque_w >= qt_estoque_maximo_w2) then
	ds_retorno_w := 'MA';
elsif (qt_estoque_w >= qt_estoque_maximo_w) then
	ds_retorno_w := 'AL';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
