CREATE OR REPLACE FUNCTION obter_situacao_guia (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_situacao_w		varchar(01);
nr_guia_w		varchar(20);
cd_senha_w		varchar(20);
ie_tipo_convenio_w	smallint;


BEGIN

select	a.nr_doc_convenio nr_guia,
	a.cd_senha cd_senha,
	obter_tipo_convenio(a.cd_convenio) ie_tipo_convenio
into STRICT	nr_guia_w,
	cd_senha_w,
	ie_tipo_convenio_w
from	procedimento_paciente a
where	a.nr_sequencia = nr_sequencia_p;

if (nr_guia_w IS NOT NULL AND nr_guia_w::text <> '') and (ie_tipo_convenio_w = 2) then
	ie_situacao_w	:= 'S';
elsif	((coalesce(nr_guia_w::text, '') = '') or (coalesce(cd_senha_w::text, '') = '')) and (ie_tipo_convenio_w = 2) then
	ie_situacao_w	:= 'P';
elsif (ie_tipo_convenio_w = 1) then
	ie_situacao_w	:= 'N';
end if;

RETURN	ie_situacao_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
