CREATE OR REPLACE FUNCTION obter_situacao_nut_elem_mat ( cd_material_p bigint, ie_ativo_p text, ie_situacao_p text) RETURNS varchar AS $body$
DECLARE


qt_regA_w		bigint;
qt_regI_w		bigint;
ie_retorno_w	varchar(1);


BEGIN

ie_retorno_w	:= ie_situacao_p;

select	count(*) qt_reg
into STRICT	qt_regA_w
from	nut_elem_material b
where	coalesce(b.ie_tipo,'NPT') = 'NPT'
and		cd_material 		 = cd_material_p
and		ie_ativo_p 			 = 'T'
and 	ie_situacao 		 = 'A';


select	count(*) qt_reg
into STRICT	qt_regI_w
from	nut_elem_material b
where	coalesce(b.ie_tipo,'NPT') = 'NPT'
and		cd_material 		 = cd_material_p
and		ie_ativo_p 		 	 = 'T'
and 	ie_situacao 		 = 'I';

if (qt_regA_w > 0) and (qt_regI_w > 0) then
	ie_retorno_w :=	'A';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
