CREATE OR REPLACE FUNCTION obter_situacao_probl_colab (nr_prescricao_p bigint, nr_seq_probl_colab_p bigint) RETURNS varchar AS $body$
DECLARE


ie_situacao_w	varchar(01)	:= 'N';
qt_problema_w	bigint;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '')	and (nr_seq_probl_colab_p IS NOT NULL AND nr_seq_probl_colab_p::text <> '')	then
	SELECT	count(*)
	INTO STRICT	qt_problema_w
	FROM	pe_prescr_probl_col
	WHERE	nr_prescricao 		= nr_prescricao_p
	and	nr_seq_probl_colab	= nr_seq_probl_colab_p;

	if (qt_problema_w	<> 0)	then
		ie_situacao_w	:= 'S';
	end if;
end if;

RETURN	ie_situacao_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
