CREATE OR REPLACE FUNCTION obter_situacao_time (cd_time_p bigint) RETURNS varchar AS $body$
DECLARE

					
ie_situacao_w	varchar(1);
ds_situacao_w varchar(10);					


BEGIN
if (cd_time_p IS NOT NULL AND cd_time_p::text <> '') then

	select coalesce(max('A'), 'I')
	into STRICT ie_situacao_w
	from protocolo_integrado_time
	where nr_sequencia = cd_time_p
	and dt_liberacao <= clock_timestamp()
	and (dt_validade >= clock_timestamp() or coalesce(dt_validade::text, '') = '');
	
	if (ie_situacao_w = 'A') then
		ds_situacao_w := obter_desc_expressao(787005);
	else
		ds_situacao_w := obter_desc_expressao(687938);
	end if;
	
else

	ds_situacao_w := '';

end if;

return ds_situacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
