CREATE OR REPLACE FUNCTION obter_situacao_tipo_reg (nr_seq_tipo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1):= 'I';
ie_situacao_w		varchar(1);

BEGIN

if (nr_seq_tipo_p IS NOT NULL AND nr_seq_tipo_p::text <> '') then
	begin

	select 	ie_situacao
	into STRICT	ie_situacao_w
	from 	qua_auditoria_tipo
	where  	nr_sequencia = nr_seq_tipo_p;

	if (ie_situacao_w = 'A') then
		 ds_retorno_w := 'A';
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
