CREATE OR REPLACE FUNCTION obter_situacao_unidade ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, dt_referencia_p timestamp ) RETURNS varchar AS $body$
DECLARE


ie_situacao_w		varchar(1) := 'A';
ie_sit_hist_w		varchar(1);
nr_seq_interno_w	bigint;

c01 CURSOR FOR
	SELECT	ie_situacao
	from	unidade_atend_hist
	where	nr_seq_unidade		= nr_seq_interno_w
	and	dt_historico		between trunc(dt_referencia_p, 'dd') and trunc(dt_referencia_p, 'dd') + 86399/86400
	order by dt_historico;


BEGIN

select	coalesce(max(nr_seq_interno), 0),
	coalesce(max(ie_situacao), 'I')
into STRICT	nr_seq_interno_w,
	ie_situacao_w
from	unidade_atendimento
where	cd_setor_atendimento	= cd_setor_atendimento_p
and	cd_unidade_basica	= cd_unidade_basica_p
and	cd_unidade_compl	= cd_unidade_compl_p;

open	c01;
loop
fetch	c01 into
	ie_sit_hist_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ie_sit_hist_w	:= ie_sit_hist_w;

	end;
end loop;
close c01;

return	coalesce(ie_sit_hist_w, ie_situacao_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
