CREATE OR REPLACE FUNCTION obter_sit_avaliacao_tec_anest (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ie_tecnica_w    varchar(1) := 'N';
nr_seq_tecnica_w bigint :=0;

BEGIN



select nr_seq_tecnica
  into STRICT nr_seq_tecnica_w
  from CIRURGIA_TEC_ANESTESICA 
  where nr_sequencia  = nr_sequencia_p;


select    coalesce(max('S'),'N')
    into STRICT    ie_tecnica_w
    from tecnica_anestesica
    where nr_sequencia = nr_seq_tecnica_w
    and ie_situacao = 'A'
    and nr_seq_tipo_avaliacao > 0;
return    ie_tecnica_w;


end;
$body$
