CREATE OR REPLACE FUNCTION obter_solic_compra (nr_lote_producao_p bigint) RETURNS varchar AS $body$
DECLARE

nr_solic_compra_w	bigint;
ds_retorno_w		varchar(4000);
C01 CURSOR FOR
	SELECT	nr_solic_compra 
	from	solic_compra 
	where 	nr_lote_producao = nr_lote_producao_p 
	order by 1;				

BEGIN 
open C01;
loop 
fetch C01 into	 
	nr_solic_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (coalesce(ds_retorno_w::text, '') = '') then 
		ds_retorno_w := nr_solic_compra_w;
	else 
		ds_retorno_w := ds_retorno_w||','||nr_solic_compra_w;
	end if;
	end;
end loop;
close C01;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
