CREATE OR REPLACE FUNCTION obter_solic_ped_exame (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_longo_w		varchar(32000);


BEGIN

begin
select 	ds_solicitacao
into STRICT 	ds_longo_w
from 	pedido_exame_externo
where	nr_sequencia = nr_sequencia_p;
exception
when others then
	ds_longo_w	:= '';
end;

return	substr(ds_longo_w,1,4000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
