CREATE OR REPLACE FUNCTION obter_solic_produto_servico ( nr_solic_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
qt_registros_w		bigint;


BEGIN

ds_retorno_w	:= wheb_mensagem_pck.get_Texto(736046); /*Produto*/
select	count(*)
into STRICT	qt_registros_w
from	solic_compra_item a,
	material b
where	a.cd_material = b.cd_material
and	a.nr_solic_compra = nr_solic_compra_p
and	b.ie_tipo_material = 4;

if (qt_registros_w > 0) then
	ds_retorno_w	:= wheb_mensagem_pck.get_Texto(736047); /*Servi√ßo*/
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
