CREATE OR REPLACE FUNCTION obter_solucao_quimio (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_solucao_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_seq_solucao
	from	prescr_solucao a,
		prescr_material b
	where	a.nr_prescricao = b.nr_prescricao
	and	a.nr_prescricao = nr_prescricao_p
	and	a.nr_seq_solucao = b.nr_sequencia_solucao
	and	(b.nr_seq_ordem_prod IS NOT NULL AND b.nr_seq_ordem_prod::text <> '')
	group by a.nr_seq_solucao;

BEGIN
ds_retorno_w := '';

open C01;
loop
fetch C01 into
	nr_seq_solucao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= nr_seq_solucao_w;
	else	ds_retorno_w	:= ds_retorno_w || ',' || nr_seq_solucao_w;
	end if;

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
