CREATE OR REPLACE FUNCTION obter_sol_qt_cot_compra (nr_solic_compra_p bigint) RETURNS bigint AS $body$
DECLARE




qt_retorno_w	integer;


BEGIN

select	count(*)
into STRICT	qt_retorno_w
from	(SELECT	a.nr_solic_compra,
				a.nr_item_solic_compra
		from	solic_compra_item a
		where	a.nr_solic_compra = nr_solic_compra_p
		and		a.qt_material > (
								SELECT	sum(x.qt_material)
								from	cot_compra_item x
								where	x.nr_solic_compra = a.nr_solic_compra
								and		x.nr_item_solic_compra = a.nr_item_solic_compra)
	
union

	select	a.nr_solic_compra,
			a.nr_item_solic_compra
	from	solic_compra_item a
 	where	a.nr_solic_compra = nr_solic_compra_p
	and		a.nr_item_solic_compra not in (
											select	x.nr_item_solic_compra
											from	cot_compra_item x
											where	a.nr_solic_compra = x.nr_solic_compra
											and		a.nr_item_solic_compra = x.nr_item_solic_compra	)) alias4;


return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
