CREATE OR REPLACE FUNCTION obter_somas_transacao_cheq ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE



retorno_w	double precision;
vl_retorno_w	double precision;


BEGIN

if (ie_opcao_p	= 'T') then

	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

		select	max(vl_transacao)
		into STRICT	vl_retorno_w
		from	movto_trans_financ
		where	nr_sequencia	= nr_sequencia_p;

	end if;

elsif (ie_opcao_p	= 'C') then

	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

		select	max(vl_cheque)
		into STRICT	vl_retorno_w
		from	cheque_cr
		where	nr_seq_cheque	= nr_sequencia_p;

	end if;

elsif (ie_opcao_p	= 'E') then

	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

		select	max(vl_lancamento)
		into STRICT	vl_retorno_w
		from	banco_extrato_lanc
		where	nr_sequencia	= nr_sequencia_p;

	end if;

end if;

retorno_w := coalesce(vl_retorno_w,0);

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
