CREATE OR REPLACE FUNCTION obter_soma_dias_autor_conv (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_autor_w 	bigint;


BEGIN

 select sum(obter_dias_entre_datas(dt_inicio_vigencia,dt_final_vigencia))
