CREATE OR REPLACE FUNCTION obter_soma_esc_prot_gord_md ( ds_lista_soma_p text, ds_delimitador_p text ) RETURNS bigint AS $body$
DECLARE


    nr_posicao_w          integer;
    nr_pos_inicial_w      integer;
    nr_pos_final_w        integer;
    nr_tam_delimitador_w  integer;
    qt_valor_w            varchar(200);
    qt_soma_w             double precision := 0;

BEGIN
    IF length(ds_lista_soma_p) > 0 THEN
        nr_posicao_w := 1;
        nr_pos_final_w := length(ds_lista_soma_p) + 1;
        nr_tam_delimitador_w := length(ds_delimitador_p);
        LOOP
            nr_pos_inicial_w := instr(ds_lista_soma_p, ds_delimitador_p, nr_posicao_w);
            IF nr_pos_inicial_w = 0 THEN
                nr_pos_inicial_w := nr_pos_final_w;
            END IF;
            qt_valor_w := substr(ds_lista_soma_p, nr_posicao_w, nr_pos_inicial_w - nr_posicao_w);
            qt_soma_w := qt_soma_w + coalesce(qt_valor_w, 0);
            EXIT WHEN nr_pos_inicial_w = nr_pos_final_w;
            nr_posicao_w := nr_pos_inicial_w + nr_tam_delimitador_w;
        END LOOP;

    END IF;

    RETURN qt_soma_w;

end;
$body$
