SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_soma_horas_requisitos ( nr_sequencia_p LATAM_REQUISITO.NR_SEQUENCIA%type, ie_geral_p text DEFAULT 'N') RETURNS bigint AS $body$
DECLARE


qt_tempo_horas_w            LATAM_REQUISITO.QT_TEMPO_HORAS%type         := 0;
qt_hora_inicializacao_w     LATAM_REQUISITO.QT_HORA_INICIALIZACAO%type  := 0;


BEGIN

SELECT coalesce(SUM(a.QT_HORA_INICIALIZACAO), 0)
INTO STRICT   qt_tempo_horas_w
FROM   LATAM_REQUISITO a
WHERE  a.NR_SEQ_SUPERIOR = nr_sequencia_p;

IF (ie_geral_p = 'S') THEN

	SELECT coalesce(MAX(a.QT_HORA_INICIALIZACAO), 0)
	INTO STRICT   qt_hora_inicializacao_w
	FROM   LATAM_REQUISITO a
	WHERE  a.NR_SEQUENCIA = nr_sequencia_p;

END IF;

RETURN qt_hora_inicializacao_w + qt_tempo_horas_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
