CREATE OR REPLACE FUNCTION obter_soma_qt_unit_mat_prescr (nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_registros_w	bigint;


BEGIN

select	coalesce(sum(qt_unitaria),0) qt_unitaria
into STRICT	qt_registros_w
from	prescr_material
where	nr_prescricao = nr_prescricao_p;


return	qt_registros_w;

end;
