CREATE OR REPLACE FUNCTION obter_somente_numero ( ds_campo_p text) RETURNS bigint AS $body$
DECLARE


ds_campo_w    	varchar(32000);
cd_campo_w    	numeric(38) := 0;
ds_campo_ww	varchar(32000);
i     		integer;


BEGIN

ds_campo_ww		:= ds_campo_p;
ds_campo_w		:= '';

if (coalesce(ds_campo_p::text, '') = '') or (ds_campo_p = '') then
	cd_campo_w	:= null;
else
	begin
	select	(ds_campo_ww)::numeric
	into STRICT	cd_campo_w
	;
	exception
		when data_exception then
		begin
		for i in 1..length(ds_campo_p) loop
	 		begin
		 	if (substr(ds_campo_p, i, 1) in ('1','2','3','4','5','6','7','8','9','0')) then
	  			ds_campo_w := ds_campo_w || substr(ds_campo_p, i, 1);
			end if;
			end;
		end loop;
		if (coalesce(ds_campo_w::text, '') = '') or (ds_campo_w = '') then
			cd_campo_w		:= null;
		else
			cd_campo_w := (substr(ds_campo_w,1,38))::numeric;
		end if;
		end;
	end;
end if;

return cd_campo_w;

end;
$body$
LANGUAGE PLPGSQL
