SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_statusurl_schematic4test (NR_SEQ_SCRIPT_P bigint) RETURNS bigint AS $body$
DECLARE

STATUS_W varchar(255);
QTD_W bigint;


BEGIN
IF (NR_SEQ_SCRIPT_P IS NOT NULL AND NR_SEQ_SCRIPT_P::text <> '') THEN
	STATUS_W := '1';

	SELECT COUNT(scr.IE_STATUS) QTD
	  INTO STRICT QTD_W
	FROM SCHEM_TEST_SCRIPT scr
	WHERE scr.NR_SEQUENCIA = NR_SEQ_SCRIPT_P
	AND scr.IE_STATUS = '0';
	
	IF (QTD_W <> 0) THEN
		STATUS_W := '0';
	END IF;

	SELECT COUNT(mai.IE_STATUS) QTD
	  INTO STRICT QTD_W
	FROM SCHEM_TEST_MAINTENANCE mai
	WHERE mai.NR_SEQ_SCRIPT = NR_SEQ_SCRIPT_P
	AND mai.IE_STATUS <> '0'
	AND mai.DS_OWNER NOT LIKE 'Robot';

	IF (QTD_W <> 0) THEN
		IF (STATUS_W <> '0') THEN
			STATUS_W := '2';
		ELSE
			STATUS_W := '3';
		END IF;
	END IF;
END IF;

RETURN STATUS_W;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
