SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_adiantamento ( nr_adiantamento_p adiantamento_dev.nr_adiantamento%type ) RETURNS varchar AS $body$
DECLARE


    ie_vinculado_w       bigint;
    ie_nao_vinculado_w   bigint;
    ie_devolvido_w       bigint;
    ie_cancelado_w       bigint;
    ie_resultado_w       varchar(2);
    vl_adiantamento_w    bigint;
    qtd_devolvido_w      bigint;

BEGIN
    -- STATUS VICULADO E NAO VINCULADO
    SELECT
        coalesce(MAX(nr_interno_conta), 0)
    INTO STRICT ie_vinculado_w
    FROM
        conta_paciente_adiant a
    WHERE
        a.nr_adiantamento = nr_adiantamento_p;

    IF ie_vinculado_w = 0 THEN
        ie_nao_vinculado_w := 1;
    END IF;
	
	--STATUS DEVOLVIDO E CANCELADO
    SELECT
        coalesce(SUM(vl_devolucao), 0)
    INTO STRICT ie_devolvido_w
    FROM
        adiantamento_dev
    WHERE
        nr_adiantamento = nr_adiantamento_p;

    SELECT
        coalesce(COUNT(vl_devolucao), 0)
    INTO STRICT qtd_devolvido_w
    FROM
        adiantamento_dev
    WHERE
        nr_adiantamento = nr_adiantamento_p;

    SELECT
        coalesce(SUM(vl_adiantamento), 0)
    INTO STRICT vl_adiantamento_w
    FROM
        adiantamento
    WHERE
        nr_adiantamento = nr_adiantamento_p;

    IF ie_devolvido_w = vl_adiantamento_w
    AND qtd_devolvido_w = 1 THEN
        ie_cancelado_w := 1;
    ELSIF ie_devolvido_w <> 0 THEN
        ie_devolvido_w := 1;
    END IF;
	
	-- PRIORIDADES: 1 - VICULADO / 2 - DEVOLVIDO / 3 - CANCELADO / 4 - NAO VINCULADO 
    IF ie_vinculado_w <> 0 THEN
        ie_resultado_w := 'V';
    ELSIF ie_devolvido_w = 1 THEN
        ie_resultado_w := 'D';
    ELSIF ie_cancelado_w = 1 THEN
        ie_resultado_w := 'C';
    ELSIF ie_nao_vinculado_w <> 0 THEN
        ie_resultado_w := 'NV';
    END IF;

    RETURN ie_resultado_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
