CREATE OR REPLACE FUNCTION obter_status_agenda_pac_cir (nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_status_agenda_w		varchar(200) := '';


BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	select	max(substr(obter_valor_dominio(83, a.ie_status_agenda),1,200))
	into STRICT	ds_status_agenda_w
	from	agenda_paciente a
	where	nr_cirurgia	= nr_cirurgia_p;
end if;

return ds_status_agenda_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
