CREATE OR REPLACE FUNCTION obter_status_alerta_api ( nr_prescricao_p prescr_medica.nr_prescricao%type, nr_seq_material_p prescr_material.nr_sequencia%type, cd_api_p alerta_api.cd_api%type, ie_detail_p text default null) RETURNS varchar AS $body$
DECLARE


	ds_retorno_w alerta_api.ie_status_requisicao%type;


BEGIN
	ds_retorno_w := 'X';

	if (coalesce(ie_detail_p, 'S') = 'S') then
		if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '' AND nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
			select 	coalesce(max(x.ie_status), 'X')
			  into STRICT 	ds_retorno_w
			  from (
					SELECT 	case a.ie_status_requisicao
							when 'F' then 'F'
							else 'E' end ie_status
					from 	alerta_api a
					where 	a.nr_prescricao = nr_prescricao_p
					and 	a.cd_api = cd_api_p
					and     (a.ie_status_requisicao IS NOT NULL AND a.ie_status_requisicao::text <> '')
					) x
			  where (SELECT max(obter_conv_meio_ext_int('MATERIAL', 'CD_MATERIAL', z.cd_material, 'LXC')) 
			           from prescr_material z
					  where z.nr_prescricao = nr_prescricao_p
					    and z.nr_sequencia = nr_seq_material_p) is not null;
		end if;
	else
		if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
			select 	coalesce(max(x.ie_status), 'X')
			  into STRICT 	ds_retorno_w
			  from (
					SELECT 	case a.ie_status_requisicao
							when 'F' then 'F'
							else 'E' end ie_status
					from 	alerta_api a
					where 	a.nr_prescricao = nr_prescricao_p
					and 	a.cd_api = cd_api_p
					and     (a.ie_status_requisicao IS NOT NULL AND a.ie_status_requisicao::text <> '')
					) x
			  where (SELECT max(obter_conv_meio_ext_int('MATERIAL', 'CD_MATERIAL', z.cd_material, 'LXC')) 
			           from prescr_material z
					  where z.nr_prescricao = nr_prescricao_p) is not null;
		end if;
	end if;

	return ds_retorno_w;

end;
$body$
