SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_alta_panora ( nr_atendimento_p bigint, cd_perfil_p bigint) RETURNS bigint AS $body$
DECLARE


ie_resultado_w				varchar(1) := 'N';								
nr_retorno_w				smallint := 0;		
nr_horas_retroativas_w		bigint;
dt_limite_previsao_w		timestamp;
ds_regra_alta_assist_w		varchar(1):= 'N';
ie_atraso_w 				double precision;
nr_status_atend_w			integer:= 0;


BEGIN

SELECT	coalesce(max('S'),'N')
into STRICT	ds_regra_alta_assist_w
FROM	regra_perm_panorama a
WHERE	a.cd_perfil = cd_perfil_p
AND EXISTS (	SELECT 1
		FROM	regra_perm_panorama_icone x
		WHERE	x.nr_seq_regra = a.nr_sequencia
		AND		cd_icone_panorama = 60  LIMIT 1) LIMIT 1;

if ( nr_atendimento_p > 0 and ds_regra_alta_assist_w = 'S') then
	Select  coalesce(max('S'),'N')
	into STRICT	ie_resultado_w
	from   	atendimento_paciente
	where  	nr_atendimento = nr_atendimento_p
	and    	coalesce(dt_alta::text, '') = '';
	
	if (ie_resultado_w = 'S') then
		Select  coalesce(max('S'),'N')
		into STRICT	ie_resultado_w
		from	atend_paciente_unidade
		where	(dt_alta_medico_setor IS NOT NULL AND dt_alta_medico_setor::text <> '')	
		and		nr_seq_interno = Obter_Atepacu_paciente(nr_atendimento_p, 'A');
		
		if (ie_resultado_w = 'S') then
			nr_retorno_w := 188; -- Alta Medica Setor - neo foi migrado o icone para o HTML5 ainda.
		end if;
			
		if (ie_resultado_w = 'N') then
			Select  coalesce(max('S'),'N')
			into STRICT	ie_resultado_w
			from    atendimento_paciente
			where   nr_atendimento = nr_atendimento_p
			and     (dt_alta_medico IS NOT NULL AND dt_alta_medico::text <> '');
			
			if (ie_resultado_w = 'S') then
				nr_retorno_w := 199; -- Alta Medica
			end if;
		end if;
					
		if (ie_resultado_w = 'N') then
			SELECT 	max(a.qt_horas_retroativo)
			INTO STRICT	nr_horas_retroativas_w
			FROM   	pan_configuracao_alta a,
					pan_configuracao_setor b
			WHERE  	a.nr_seq_setor = b.nr_sequencia
			AND		B.cd_setor_atendimento = Obter_Setor_Atendimento(nr_atendimento_p);

            if (nr_horas_retroativas_w = 0 or coalesce(nr_horas_retroativas_w::text, '') = '')then
                dt_limite_previsao_w := clock_timestamp() + interval '1 days';
            else
                dt_limite_previsao_w := clock_timestamp() + (nr_horas_retroativas_w/24);
            end if;

			Select 	coalesce(max('S'),'N')
			into STRICT	ie_resultado_w
			from   	atendimento_paciente
			where  	nr_atendimento = nr_atendimento_p
			and    	(dt_previsto_alta IS NOT NULL AND dt_previsto_alta::text <> '')
			and    	DT_PREVISTO_ALTA between clock_timestamp() and dt_limite_previsao_w;
			
			if (ie_resultado_w = 'S') then
				nr_retorno_w := 278; -- Alta Prevista
			end if;
			
			if (ie_resultado_w = 'N') then
			 select round(((clock_timestamp() - a.dt_previsto_alta)*24),2) ie_atraso
			  into STRICT ie_atraso_w
			  from atendimento_paciente a
			 where nr_atendimento =  nr_atendimento_p
			  and coalesce(dt_alta::text, '') = '' 
			   and (dt_previsto_alta IS NOT NULL AND dt_previsto_alta::text <> '')
			;
				if (ie_atraso_w > 4 and ie_atraso_w < 8) then
				  nr_retorno_w:= 279;
				
				elsif (ie_atraso_w > 8) then
				  nr_retorno_w:= 280;
				 end if;
			end if;
		end if;
	end if;
end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_status_alta_panora ( nr_atendimento_p bigint, cd_perfil_p bigint) FROM PUBLIC;

