SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_ant_processo ( nr_seq_processo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_w	varchar(15)	:= 'X';
dt_paciente_w	timestamp;
dt_preparo_w	timestamp;
dt_leitura_w	timestamp;
dt_retirada_w	timestamp;
dt_higienizacao_w	timestamp;
dt_dispensacao_w	timestamp;
dt_processo_w	timestamp;


BEGIN
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then

	select	max(dt_paciente),
		max(dt_retirada),
		max(dt_leitura),
		max(dt_preparo),
		max(dt_higienizacao),
		max(dt_dispensacao),
		max(dt_processo)
	into STRICT	dt_paciente_w,
		dt_retirada_w,
		dt_leitura_w,
		dt_preparo_w,
		dt_higienizacao_w,
		dt_dispensacao_w,
		dt_processo_w
	from	adep_processo
	where	nr_sequencia = nr_seq_processo_p;

	if (dt_paciente_w IS NOT NULL AND dt_paciente_w::text <> '') then
		ie_status_w	:= 'A';
	elsif (dt_retirada_w IS NOT NULL AND dt_retirada_w::text <> '') then
		ie_status_w	:= 'R';
	elsif (dt_leitura_w IS NOT NULL AND dt_leitura_w::text <> '') and (coalesce(dt_preparo_w::text, '') = '') then
		ie_status_w	:= 'L';
	elsif (dt_leitura_w IS NOT NULL AND dt_leitura_w::text <> '') and (dt_preparo_w IS NOT NULL AND dt_preparo_w::text <> '') and (dt_leitura_w > dt_preparo_w) then
		ie_status_w	:= 'L';
	elsif (dt_preparo_w IS NOT NULL AND dt_preparo_w::text <> '') then
		ie_status_w	:= 'P';
	elsif (dt_higienizacao_w IS NOT NULL AND dt_higienizacao_w::text <> '') then
		ie_status_w	:= 'H';
	elsif (dt_dispensacao_w IS NOT NULL AND dt_dispensacao_w::text <> '') then
		ie_status_w	:= 'D';
	elsif (dt_processo_w IS NOT NULL AND dt_processo_w::text <> '') then
		ie_status_w	:= 'G';
	end if;

	if (dt_paciente_w IS NOT NULL AND dt_paciente_w::text <> '') and (ie_status_w <> 'A')then
		ie_status_w	:= 'A';
	elsif (dt_retirada_w IS NOT NULL AND dt_retirada_w::text <> '')  and (ie_status_w <> 'R')then
		ie_status_w	:= 'R';
	elsif (dt_leitura_w IS NOT NULL AND dt_leitura_w::text <> '') and (coalesce(dt_preparo_w::text, '') = '')  and (ie_status_w <> 'L')then
		ie_status_w	:= 'L';
	elsif (dt_leitura_w IS NOT NULL AND dt_leitura_w::text <> '') and (dt_preparo_w IS NOT NULL AND dt_preparo_w::text <> '') and (dt_leitura_w > dt_preparo_w)  and (ie_status_w <> 'L')then
		ie_status_w	:= 'L';
	elsif (dt_preparo_w IS NOT NULL AND dt_preparo_w::text <> '')  and (ie_status_w <> 'P')then
		ie_status_w	:= 'P';
	elsif (dt_higienizacao_w IS NOT NULL AND dt_higienizacao_w::text <> '')  and (ie_status_w <> 'H')then
		ie_status_w	:= 'H';
	elsif (dt_dispensacao_w IS NOT NULL AND dt_dispensacao_w::text <> '')  and (ie_status_w <> 'D')then
		ie_status_w	:= 'D';
	elsif (dt_processo_w IS NOT NULL AND dt_processo_w::text <> '')  and (ie_status_w <> 'G')then
		ie_status_w	:= 'G';
	end if;

end if;

return ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
