SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_aprov_oc ( nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
dt_aprovacao_w		timestamp;			
ie_status_w		varchar(1);	
qt_registro_w		bigint;
	

BEGIN 
 
select	dt_aprovacao 
into STRICT	dt_aprovacao_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p;
 
if (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then 
	ie_status_w := 'A';
else 
	select	count(*) 
	into STRICT	qt_registro_w 
	from ( 
	SELECT	a.ie_aprov_reprov ie_aprov_reprov 
	from	processo_aprov_compra a, 
		pessoa_fisica b 
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
	and	coalesce(a.cd_cargo::text, '') = '' 
	and	a.nr_sequencia in ( 
			SELECT	distinct nr_seq_aprovacao 
			from	ordem_compra_item 
			where	nr_ordem_compra = nr_ordem_compra_p) 
	
union all
 
	select	a.ie_aprov_reprov 
	from	processo_aprov_compra a, 
		cargo b 
	where	a.cd_cargo = b.cd_cargo 
	and	a.nr_sequencia in ( 
		select	distinct nr_seq_aprovacao 
		from	ordem_compra_item 
		where	nr_ordem_compra = nr_ordem_compra_p)) alias4 
	where	ie_aprov_reprov = 'A';
	 
	if (qt_registro_w > 0) then 
		ie_status_w := 'P';
	else 
		ie_status_w := 'N';
	end if;
	 
end if;
 
return	ie_status_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
