CREATE OR REPLACE FUNCTION obter_status_atend_equipe ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);


BEGIN
select
   case
      when(a.dt_chegada_base IS NOT NULL AND a.dt_chegada_base::text <> '') then 'J12'
	  when(a.dt_saida_local IS NOT NULL AND a.dt_saida_local::text <> '') then 'J11'
	  when(a.dt_chegada_local IS NOT NULL AND a.dt_chegada_local::text <> '') then 'J10'
	  when(a.dt_partida IS NOT NULL AND a.dt_partida::text <> '') then 'J9'
   end
into STRICT   	ds_retorno_w
from 	eme_reg_resposta a,
	eme_regulacao b
where 	a.nr_seq_reg = b.nr_sequencia
and	b.nr_sequencia = nr_sequencia_p
and	a.nr_sequencia = 	(SELECT max(nr_sequencia)
			from   eme_reg_resposta
			where  nr_seq_reg = nr_sequencia_p);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
