CREATE OR REPLACE FUNCTION obter_status_atend_prescr_proc (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_motivo_baixa_w		smallint := 0;
ie_status_proc_w		varchar(2) := '';

C01 CURSOR FOR
	SELECT coalesce(cd_motivo_baixa,0)
	from prescr_procedimento
	where nr_prescricao = nr_prescricao_p;

BEGIN
open C01;
Loop
Fetch C01 into cd_motivo_baixa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	if (cd_motivo_baixa_w = 0) then
		if (coalesce(position('P' in ie_status_proc_w),0) <= 0) then
			ie_status_proc_w := ie_status_proc_w || 'P';
		end if;
	else
		if (coalesce(position('B' in ie_status_proc_w),0) <= 0) then
			ie_status_proc_w := ie_status_proc_w || 'B';
		end if;
	end if;
end loop;
close C01;

RETURN ie_status_proc_w;

END;
$body$
