CREATE OR REPLACE FUNCTION obter_status_atend_prescr ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(50);
ie_retorno_w			smallint;
ie_material_nao_atend_w		varchar(1);
ie_atend_material_w		varchar(1);

C01 CURSOR FOR
	SELECT	cd_motivo_baixa
	from	prescr_material
	where	nr_prescricao = nr_prescricao_p;


BEGIN

ie_material_nao_atend_w := 'N';
ie_atend_material_w 	:= 'N';

open C01;
loop
fetch C01 into
	ie_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	if (coalesce(ie_retorno_w,0) > 0) then
		ie_atend_material_w := 'S';
	elsif (coalesce(ie_retorno_w,0) = 0) then
		ie_material_nao_atend_w := 'S';
	end if;
end loop;
close C01;

if (ie_atend_material_w = 'S') and (ie_material_nao_atend_w = 'N') then
	ds_retorno_w := wheb_mensagem_pck.get_texto(309951); -- Total
elsif (ie_atend_material_w = 'S') and (ie_material_nao_atend_w = 'S') then
	ds_retorno_w := wheb_mensagem_pck.get_texto(309952); -- Parcial
elsif (ie_atend_material_w = 'N') and (ie_material_nao_atend_w = 'S') then
	ds_retorno_w := wheb_mensagem_pck.get_texto(309953); -- NÃ£o atendida
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
