SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_auditoria_conta ( nr_interno_conta_p bigint, ie_cod_desc_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
qt_auditoria_w		bigint;
qt_auditoria_lib_w	bigint;


BEGIN

ds_retorno_w	:= 'N';

if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then
	select	count(*)
	into STRICT	qt_auditoria_w
	from	auditoria_conta_paciente
	where	nr_interno_conta = nr_interno_conta_p;

	if (qt_auditoria_w > 0) then
		select	count(*)
		into STRICT	qt_auditoria_lib_w
		from	auditoria_conta_paciente
		where	nr_interno_conta = nr_interno_conta_p
		and	coalesce(dt_liberacao::text, '') = '';

		if (qt_auditoria_lib_w > 0) then
			ds_retorno_w	:= 'S';
		else
			ds_retorno_w	:= 'F';
		end if;
	end if;
end if;

if (ie_cod_desc_p = 'D') then
	if (ds_retorno_w = 'N') then
		ds_retorno_w	:= wheb_mensagem_pck.get_Texto(311876); /*'Sem auditoria';*/
	elsif (ds_retorno_w = 'S') then
		ds_retorno_w	:= wheb_mensagem_pck.get_Texto(311877); /*'Em auditoria';*/
	elsif (ds_retorno_w = 'F') then
		ds_retorno_w	:= wheb_mensagem_pck.get_Texto(311878); /*'Finalizada';*/
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
