CREATE OR REPLACE FUNCTION obter_status_autor_cirurgia (nr_sequencia_p bigint, ie_codigo_desc_p text) RETURNS varchar AS $body$
DECLARE


/*ie_codigo_desc_p D - Descrição C- Código */

ds_status_w		varchar(80);
dt_liberacao_w	timestamp;
dt_fim_cotacao_w	timestamp;
dt_autorizacao_w	timestamp;
ds_motivo_negativa_w varchar(255);


BEGIN

select	dt_liberacao,
	dt_fim_cotacao,
	dt_autorizacao,
	ds_motivo_negativa
into STRICT
	dt_liberacao_w,
	dt_fim_cotacao_w,
	dt_autorizacao_w,
	ds_motivo_negativa_w
from	autorizacao_cirurgia
where	nr_sequencia	= nr_sequencia_p;

if (ie_codigo_desc_p = 'C') then
	begin
	if (ds_motivo_negativa_w IS NOT NULL AND ds_motivo_negativa_w::text <> '') then
		ds_status_w		:= 'R';
	elsif (dt_autorizacao_w IS NOT NULL AND dt_autorizacao_w::text <> '') then
		ds_status_w		:= 'A';
	elsif (dt_fim_cotacao_w IS NOT NULL AND dt_fim_cotacao_w::text <> '') then
		ds_status_w		:= 'PE';
	elsif (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
		ds_status_w		:= 'PC';
	else
		ds_status_w		:= 'NL';
	end if;
	end;
else
	begin
	if (ds_motivo_negativa_w IS NOT NULL AND ds_motivo_negativa_w::text <> '') then
		ds_status_w		:= obter_desc_expressao(487440);--'Rejeitada pelo convênio';
	elsif (dt_autorizacao_w IS NOT NULL AND dt_autorizacao_w::text <> '') then
		ds_status_w		:= obter_desc_expressao(330627); --'Autorizada';
	elsif (dt_fim_cotacao_w IS NOT NULL AND dt_fim_cotacao_w::text <> '') then
		ds_status_w		:= obter_desc_expressao(487439);--'Pendente de envio';
	elsif (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
		ds_status_w		:= obter_desc_expressao(331360); --'Pendente de cotacao';
	else
		ds_status_w		:= obter_desc_expressao(331231);--'Não liberada';
	end if;
	end;
end if;

return ds_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
