CREATE OR REPLACE FUNCTION obter_status_autor_consulta (nr_seq_agenda_consulta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_sequencia_autor_w	bigint;
ie_interno_w		varchar(2)	:= null;
ds_estagio_w		varchar(60)	:= null;
ds_retorno_w		varchar(60)	:= null;


BEGIN

if (nr_seq_agenda_consulta_p IS NOT NULL AND nr_seq_agenda_consulta_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_autor_w
	from	autorizacao_convenio
	where	nr_seq_agenda_consulta	= nr_seq_agenda_consulta_p;

	if (nr_sequencia_autor_w IS NOT NULL AND nr_sequencia_autor_w::text <> '') then

		select	b.ie_interno,
			b.ds_estagio
		into STRICT	ie_interno_w,
			ds_estagio_w
		FROM autorizacao_convenio a
LEFT OUTER JOIN estagio_autorizacao b ON (a.nr_seq_estagio = b.nr_sequencia)
WHERE a.nr_sequencia		= nr_sequencia_autor_w;

		if (ie_opcao_p	= 'C') then
			ds_retorno_w	:= ie_interno_w;
		elsif (ie_opcao_p	= 'D') then
			ds_retorno_w	:= ds_estagio_w;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
