CREATE OR REPLACE FUNCTION obter_status_aval_nursing_care ( nr_sequencia_p nursing_care_item.nr_sequencia%TYPE, nr_seq_versao_p nursing_care_item.nr_seq_versao%TYPE, cd_result_p nursing_care_result.nr_sequencia%TYPE, cd_result_f nursing_care_result.nr_sequencia%TYPE ) RETURNS bigint AS $body$
DECLARE


    count_same_score_w bigint := 0;

BEGIN
    SELECT
        COUNT(1)
    INTO STRICT count_same_score_w
    FROM
        nursing_care_item     nci
        INNER JOIN nursing_care_result   ncr ON nci.nr_sequencia = ncr.nr_seq_item
    WHERE
        nci.nr_sequencia = nr_sequencia_p
        AND nci.nr_seq_versao = nr_seq_versao_p
        AND ncr.nr_sequencia = cd_result_p
        AND coalesce(nci.nr_seq_item_sup::text, '') = ''
        AND ncr.ie_situacao = 'A'
        AND EXISTS (
            SELECT
                1
            FROM
                nursing_care_item     ncif
                INNER JOIN nursing_care_result   ncrf ON ncif.nr_sequencia = ncrf.nr_seq_item
            WHERE
                ncrf.nr_sequencia = cd_result_f
                AND ncif.nr_seq_item_sup = nci.nr_sequencia
                AND ncif.nr_seq_versao = nci.nr_seq_versao
                AND ncrf.qt_ponto = ncr.qt_ponto
                AND (ncif.nr_seq_item_sup IS NOT NULL AND ncif.nr_seq_item_sup::text <> '')
                AND ncr.ie_situacao = 'A'
        );

    RETURN count_same_score_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
