CREATE OR REPLACE FUNCTION obter_status_bomba_inf_graph (nr_cirurgia_p equipamento_cirurgia.nr_cirurgia%type, nr_seq_agente_p bomba_infusao.nr_seq_agente%type) RETURNS varchar AS $body$
DECLARE


  oResult         bomba_infusao.ie_status%type;
  vStatusDS       bomba_infusao.nr_sequencia%type;
  vStatusST       bomba_infusao.nr_sequencia%type;
  vStatusCN       bomba_infusao.nr_sequencia%type;
  vIeQuestionavel bomba_infusao.nr_sequencia%type;


BEGIN

    with status_pump as (select case
               when bii.ie_status in ('DS') then
                count(bii.ie_status)
             end ie_status_ds,
             case
               when bii.ie_status in ('ST') then
                count(bii.ie_status)
             end ie_status_st,
             case
               when bii.ie_status in ('IP', 'IS', 'BL', 'TC', 'KV', 'KT') then
                count(bii.ie_status)
             end ie_status_cn,
             case
               when coalesce(bii.ie_estado_questionavel, 'N') = 'S' then
                count(bii.ie_estado_questionavel)
             end ie_questionavel
        from bomba_infusao bi
       inner join bomba_infusao_interface bii
          on bi.nr_seq_bomba_interface = bii.nr_sequencia
       inner join equipamento_cirurgia ec
          on ec.nr_sequencia = bi.nr_seq_equip_cirurgia
       where bi.ie_ativo = 'S'
         and ec.nr_cirurgia = nr_cirurgia_p
         and bi.nr_seq_agente = nr_seq_agente_p
       group by bii.ie_status, bii.ie_estado_questionavel)
    select sum(coalesce(status_pump.ie_status_ds, 0)) ie_status_ds,
           sum(coalesce(status_pump.ie_status_st, 0)) ie_status_st,
           sum(coalesce(status_pump.ie_status_cn, 0)) ie_status_cn,
           sum(coalesce(status_pump.ie_questionavel, 0)) ie_questionavel
      into STRICT vstatusds,
           vstatusst, 
           vstatuscn, 
           viequestionavel
      from status_pump;

  if (vStatusCN > 0 and vStatusDS = 0 and vStatusST = 0) and
     vIeQuestionavel = 0 then
    oResult := 'CN';
  elsif vStatusDS > 0 and vIeQuestionavel = 0 then
    oResult := 'DS';
  elsif (vStatusST > 0 and vStatusDS = 0 and vStatusCN >= 0) and
        vIeQuestionavel = 0 then
    oResult := 'NI';
  elsif vIeQuestionavel > 0 then
    oResult := 'QN';
  end if;

  return(oResult);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
