SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_cal_escalas ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(05) := 'RE';
qtd_registros_w		bigint;
qtd_registros_aprov_w	bigint;
dt_liberacao_w          timestamp;


BEGIN

select dt_liberacao
into STRICT   dt_liberacao_w
from 	man_calibracao
where nr_sequencia = nr_sequencia_p;

if (coalesce(dt_liberacao_w::text, '') = '') then
	ds_retorno_w 	:= 'AN';
	return ds_retorno_w;
end if;

select	count(*)
into STRICT	qtd_registros_w
from	man_calibracao_escala
where	nr_seq_calibracao = nr_sequencia_p;

select	count(*)
into STRICT	qtd_registros_aprov_w
from	man_calibracao_escala
where 	nr_seq_calibracao = nr_sequencia_p
and    	substr(man_obter_se_cal_escala_aprov(nr_sequencia),1,1) = 'S';

if (qtd_registros_w = qtd_registros_aprov_w) then
	ds_retorno_w := 'AP';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
