CREATE OR REPLACE FUNCTION obter_status_camera_atend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE
 ie_status_camera_w	varchar(1);

BEGIN
  ie_status_camera_w := 0;
  if (coalesce(nr_atendimento_p,0) > 0) then
    select pat.STATUS_CAMERA
    into STRICT ie_status_camera_w
    from unidade_atendimento ua,
    setor_atendimento sa,
    table(search_panorama_content.get_data(ua.cd_setor_atendimento,ua.cd_unidade_basica,ua.nr_seq_interno)) pat
    where ua.nr_atendimento = nr_atendimento_p
    and sa.cd_setor_atendimento = ua.cd_setor_atendimento
    and ua.cd_setor_atendimento = sa.cd_setor_atendimento
    and ua.dt_entrada_unidade = (SELECT max(b.dt_entrada_unidade) from unidade_atendimento b where b.nr_atendimento = nr_atendimento_p);
  end if;
return ie_status_camera_w;
end;
