CREATE OR REPLACE FUNCTION obter_status_checklist_unid (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_aplicaaco_checklist_w	sl_grupo_check_list.ie_aplicacao_checklist%type;
ds_retorno_w			varchar(1);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select 	max(a.ie_aplicacao_checklist)
	into STRICT	ds_retorno_w
	from 	sl_check_list_unid_item c,
		sl_item_check_list b,
		sl_grupo_check_list a,
		sl_check_list_unid d
	where 	c.nr_seq_item_check_list = b.nr_sequencia
	and    	b.nr_seq_grupo_check_list = a.nr_sequencia
	and    	c.nr_seq_checklist = d.nr_sequencia
	and    	d.nr_sequencia = nr_sequencia_p
	and	(d.dt_liberacao IS NOT NULL AND d.dt_liberacao::text <> '');

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
