SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_cheque_cpa (nr_seq_cheque_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* Status

 1 - Aberto
 2 - Compensado
 3 - Cancelado
 4 - Impresso

*/
dt_compensacao_w	timestamp;
dt_cancelamento_w	timestamp;
dt_impressao_w		timestamp;
ie_status_w		varchar(255);
ds_status_w		varchar(255);
ds_retorno_w		varchar(255);


BEGIN

select	max(a.dt_compensacao),
	max(a.dt_cancelamento),
	max(a.dt_impressao)
into STRICT	dt_compensacao_w,
	dt_cancelamento_w,
	dt_impressao_w
from	cheque a
where	nr_sequencia = nr_seq_cheque_p;

if (coalesce(dt_compensacao_w::text, '') = '') and (coalesce(dt_cancelamento_w::text, '') = '') and (coalesce(dt_impressao_w::text, '') = '') then
	ie_status_w	:= '1';
	ds_status_w	:= obter_desc_expressao(312354);--'Aberto';
elsif (dt_compensacao_w IS NOT NULL AND dt_compensacao_w::text <> '')  then
	ie_status_w	:= '2';
	ds_status_w	:= obter_desc_expressao(330731);--'Compensado';
elsif (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then
	ie_status_w	:= '3';
	ds_status_w	:= obter_desc_expressao(322155);--'Cancelado';
elsif (dt_impressao_w IS NOT NULL AND dt_impressao_w::text <> '') then
	ie_status_w	:= '4';
	ds_status_w	:= obter_desc_expressao(313319);--'Impresso';
end if;

if (ie_opcao_p	= 'C') then
	ds_retorno_w	:= ie_status_w;
elsif (ie_opcao_p	= 'D') then
	ds_retorno_w	:= ds_status_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
