CREATE OR REPLACE FUNCTION obter_status_cirur_paciente ( nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE

ie_status_cirurgia_w	varchar(3) := -1;


BEGIN

if (coalesce(nr_cirurgia_p,0) > 0) then
	select	coalesce(max(ie_status_cirurgia),-1)
	into STRICT	ie_status_cirurgia_w
	from	cirurgia
	where	nr_cirurgia = nr_cirurgia_p;
end if;

return 	ie_status_cirurgia_w;

end;
$body$
LANGUAGE PLPGSQL
