SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_consulta_oftalmo ( nr_seq_status_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_status_w	varchar(255);
ds_cor_w	varchar(15);
ds_retorno_w	varchar(1000):=null;


BEGIN

if (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	select 	ds_status,
		coalesce(ds_cor,'clwhite') ds_cor
	into STRICT	ds_status_w,
		ds_cor_w
	from   	oft_status_consulta
	where	nr_sequencia = nr_seq_status_p;

	if (ie_opcao_p = 'S') then
		ds_retorno_w := ds_status_w;
	elsif (ie_opcao_p = 'C') then
		ds_retorno_w := ds_cor_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
