SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_contato_lista ( nr_seq_lista_espera_p bigint) RETURNS varchar AS $body$
DECLARE


nr_situacao_atual_w  	bigint;
nr_seq_w				bigint;
ie_contato_realizado_w	varchar(2);
ie_tipo_contato_w		varchar(2);
ds_retorno_w				varchar(2);


BEGIN

/*AR - Aguardando resposta
 PN - Paciente não encontrado
 PC - Paciente ciente
 PJ - Paciente já realizou*/
select	max(nr_sequencia)
into STRICT 	nr_seq_w
from 	ag_lista_espera_contato
where 	nr_seq_lista_espera = nr_seq_lista_espera_p
and 	coalesce(dt_inativacao::text, '') = ''
and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');	

if (nr_seq_w IS NOT NULL AND nr_seq_w::text <> '')then
	select	max(nr_seq_situacao_atual),
			max(ie_contato_realizado)
	into STRICT 	nr_situacao_atual_w,
			ie_contato_realizado_w
	from 	ag_lista_espera_contato
	where 	nr_sequencia = nr_seq_w;
end if;

if (nr_situacao_atual_w IS NOT NULL AND nr_situacao_atual_w::text <> '')then
	select	max(ie_tipo_contato)
	into STRICT	ie_tipo_contato_w
	from	lista_espera_situacao_cont
	where	nr_sequencia = 	nr_situacao_atual_w
	and	 	ie_situacao = 'A';
end if;

if (ie_tipo_contato_w IS NOT NULL AND ie_tipo_contato_w::text <> '') then
	begin
		ds_retorno_w := ie_tipo_contato_w;
	end;
else if (coalesce( ie_contato_realizado_w,'X') = 'S') then
	begin
		ds_retorno_w := 'PC';
	end;
else	
		ds_retorno_w := '';
end if;
end if;	

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_status_contato_lista ( nr_seq_lista_espera_p bigint) FROM PUBLIC;

