SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_conta ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_status_conta_w	varchar(1);
ds_status_conta_w	varchar(255);


BEGIN

if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then
	/*Coelho - OS110404*/

	Select	to_char(a.ie_status_acerto),
			b.ds_valor_dominio
	into STRICT	ie_status_conta_w,
			ds_status_conta_w
	from	valor_dominio b,
			conta_paciente a
	where	a.nr_interno_conta 	= nr_interno_conta_p
	and		to_char(a.ie_status_acerto)	= b.vl_dominio
	and		b.cd_dominio = 49;

	if (ie_opcao_p = 'C') then
		RETURN ie_status_conta_w;
	else
		return ds_status_conta_w;
	end if;
else
	RETURN null;
end if;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
